<?phpfunction jejuu($a){    return json_encode($a,JSON_UNESCAPED_UNICODE);}function callback_jejuu($a,$b){    if(isset($b['callback'])){        return $b['callback'].'('.jejuu($a).')';    }else{        return jejuu($a);    }}function getCTypeName($CType){    if($CType === 's'){        return '简单纸板';    }elseif($CType === 'c'){        return '纸箱纸板';    }elseif($CType === 'x'){        return '纸箱';    }elseif($CType === 't'){        return '淘宝箱';    }else{        return '';    }}//团购产品价格统一格式化function PriceFormat($price){    $price = sprintf('%.3f',$price);    if(substr($price,-1) === '0'){        return substr($price,0,strlen($price) - 1);    }else{        return $price;    }}//对 CusId 保密处理function CusIdSecret($CusId){    $length = strlen($CusId);    if($length === 1){        return '*';    }elseif($length === 2){        return substr($CusId,0,1).'*';    }else{        return substr($CusId,0,1).str_repeat('*',$length - 2).substr($CusId,$length - 1,1);    }}